    private void txtSomarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_txtSomarActionPerformed
        double n1 = Double.parseDouble(txtNumero1.getText());
        double n2 = Double.parseDouble(txtNumero2.getText());
        double resultado = n1 + n2;

        lbRsultado.setText("A soma é =" + String.format("%.2f", resultado));
    }//GEN-LAST:event_txtSomarActionPerformed

    private void txtLimparActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_txtLimparActionPerformed
        txtNumero1.setText("");
        txtNumero2.setText("");
        lbRsultado.setText("");

        txtNumero1.requestFocus();
    }//GEN-LAST:event_txtLimparActionPerformed

