    private void txtDiminuirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_txtDiminuirActionPerformed
        double n1 = Double.parseDouble(txtNumero1.getText());
        double n2 = Double.parseDouble(txtNumero2.getText());
        double resultado = n1 - n2;

        lbRsultado.setText("A Subtração é =" + String.format("%.2f", resultado));        // TODO add your handling code here:
    }//GEN-LAST:event_txtDiminuirActionPerformed

    private void txtDividirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_txtDividirActionPerformed
        double n1 = Double.parseDouble(txtNumero1.getText());
        double n2 = Double.parseDouble(txtNumero2.getText());
        if (n2 == 0) {
            lbRsultado.setText("Erro: Divisão por zero não é permitida.");
        } else {
            double resultado = n1 / n2;
            lbRsultado.setText("A Divisão é = " + String.format("%.2f", resultado));
        }
   
    }//GEN-LAST:event_txtDividirActionPerformed

    private void txtMultiplicarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_txtMultiplicarActionPerformed
        double n1 = Double.parseDouble(txtNumero1.getText());
        double n2 = Double.parseDouble(txtNumero2.getText());
        double resultado = n1 * n2;

        lbRsultado.setText("A Multiplicação é =" + String.format("%.2f", resultado));        // TODO add your handling code here:
    }//GEN-LAST:event_txtMultiplicarActionPerformed

